  var lockKey = "counter_lock"
  var counterKey = "counter"

  // lấy lock
  resp := client.SetNX(lockKey, 1, time.Second*5)
  lockSuccess, err := resp.Result()

  if err != nil || !lockSuccess {
    fmt.Println(err, "lock result: ", lockSuccess)
    return
